use std::collections::HashSet;

const INPUT: &'static str = "";

fn start_of_package() {
    //When we get to unique combination
    //the 3 last characters won't be added to the idx yet
    let mut idx = 3;

    // for chrs in INPUT.as_bytes().windows(4) {
    //     idx += 1;
    //     if chrs[0] != chrs[1]
    //         && chrs[0] != chrs[2]
    //         && chrs[0] != chrs[3]
    //         && chrs[1] != chrs[2]
    //         && chrs[1] != chrs[3]
    //         && chrs[2] != chrs[3]
    //     {
    //         break;
    //     }
    // }
    'outer: for chrs in INPUT.as_bytes().windows(4) {
        idx += 1;
        let mut hs_set = HashSet::<&u8>::with_capacity(4);
        for chr in chrs {
            if !hs_set.insert(chr) {
                continue 'outer;
            }
        }
        break;
    }
    print!("{idx}");
}
fn start_of_message() {
    //When we get to unique combination
    //the 3 last characters won't be added to the idx yet
    let mut idx = 13;

    'outer: for chrs in INPUT.as_bytes().windows(14) {
        idx += 1;
        let mut hs_set = HashSet::<&u8>::with_capacity(14);
        for chr in chrs {
            if !hs_set.insert(chr) {
                continue 'outer;
            }
        }
        break;
    }
    print!("{idx}");
}
fn main() {
    start_of_message();
}
