use std::collections::HashSet;

const INPUT: &'static str = "";

fn first_package(length: usize) {
    //When we get to unique combination
    //the last characters won't be added to the idx yet
    let mut idx = length - 1;

    'outer: for chrs in INPUT.as_bytes().windows(length) {
        idx += 1;
        let mut hs_set = HashSet::<&u8>::with_capacity(length);
        for chr in chrs {
            if !hs_set.insert(chr) {
                continue 'outer;
            }
        }
        break;
    }
    println!("{idx}");
}
fn main() {
    first_package(4);
    first_package(14);
}
